import React, { useEffect, useState } from 'react';
import Background from './Background';

interface HeroProps {
  scrollToProjects: () => void;
}

const Hero: React.FC<HeroProps> = ({ scrollToProjects }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <Background className="min-h-screen flex items-center justify-center">
      <div className="container mx-auto px-4">
        <div className={`text-center transform transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold mb-6 leading-tight">
            <span className="block text-white">AI-Powered Solutions,</span>
            <span className="text-black">Delivered</span>
          </h1>

          <p className="text-lg md:text-xl text-gray-300 max-w-2xl mx-auto mb-10">
            We use cutting-edge AI technology to build intuitive and modern solutions, ensuring a seamless experience for our users.
          </p>

          <button 
            onClick={scrollToProjects}
            className="px-8 py-3 rounded-full bg-gradient-to-r from-custom-pastel-green via-custom-soft-pink to-custom-teal text-black hover:opacity-90 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-lg font-medium animate-bounce"
            style={{ animationDuration: '2s' }}
          >
            Discover Daily Bios
          </button>
        </div>
      </div>
    </Background>
  );
};

export default Hero;
