import React, { useEffect, useState } from 'react';

interface HeroProps {
  scrollToProjects: () => void;
}

const Hero: React.FC<HeroProps> = ({ scrollToProjects }) => {
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    setIsVisible(true);
  }, []);

  return (
    <section className="min-h-screen flex items-center justify-center relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-b from-black via-black to-black opacity-80 z-0"></div>
      <div className="absolute inset-0 bg-gradient-custom animate-gradient-xy opacity-60 z-0"></div>

      <div className="absolute top-1/4 left-1/4 w-64 h-64 rounded-full bg-custom-teal opacity-10 blur-3xl animate-pulse"></div>
      <div className="absolute bottom-1/3 right-1/4 w-80 h-80 rounded-full bg-custom-orange opacity-10 blur-3xl animate-pulse" style={{ animationDelay: '1s' }}></div>

      <div className="container mx-auto px-4 z-10">
        <div className={`text-center transform transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <h1 className="text-4xl md:text-6xl lg:text-7xl font-bold mb-6 leading-tight">
            <span className="block text-white">AI-Powered Solutions,</span>
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-custom-teal via-custom-orange to-custom-hot-pink drop-shadow-[0_2px_2px_rgba(0,0,0,0.8)] brightness-150">Delivered</span>
          </h1>

          <p className="text-lg md:text-xl text-gray-300 max-w-2xl mx-auto mb-10">
            We use cutting-edge AI technology to build intuitive and modern solutions, ensuring a seamless experience for our users.
          </p>

          <button 
            onClick={scrollToProjects}
            className="px-8 py-3 rounded-full bg-white text-black hover:bg-custom-teal hover:text-white transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-lg font-medium"
          >
            Discover Daily Bios
          </button>
        </div>
      </div>
    </section>
  );
};

export default Hero;
