import React, { useEffect, useRef, useState } from 'react';
import IPhonePreview from './IPhonePreview';

interface ProjectsProps {
  openContactModal: () => void;
}

const Projects: React.FC<ProjectsProps> = ({ openContactModal }) => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.unobserve(entry.target);
        }
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  return (
    <section id="projects" ref={sectionRef} className="py-8 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-b from-black to-purple-900 z-0"></div>

      <div className="container mx-auto px-4 relative z-10" style={{ paddingTop: '8rem' }}>
        <div className={`mb-4 transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <h2 className="text-3xl md:text-4xl font-bold mb-2">
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">Featured Project</span>
          </h2>

          <p className="text-lg max-w-3xl text-gray-300">
            Discover Daily Bios, our innovative AI-powered application that delivers personalized biographies to inspire your growth journey.
          </p>
        </div>

        <div className={`transition-all duration-1000 delay-300 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <div className="flex flex-col lg:flex-row gap-8">
            <div className="lg:w-1/2 flex flex-col justify-center text-left">
              <h3 className="text-2xl font-bold mb-2 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                Daily Bios
              </h3>

              <p className="text-gray-300 mb-3">
                An AI-powered platform that provides three daily biographies tailored to your personal growth goals, focusing on communication, creativity, and leadership.
              </p>

              <ul className="mb-4 space-y-1">
                <li className="flex items-start">
                  <span className="text-purple-400 mr-2">✓</span>
                  <span className="text-gray-300">Personalized biography recommendations</span>
                </li>
                <li className="flex items-start">
                  <span className="text-purple-400 mr-2">✓</span>
                  <span className="text-gray-300">Daily inspiration for personal growth</span>
                </li>
                <li className="flex items-start">
                  <span className="text-purple-400 mr-2">✓</span>
                  <span className="text-gray-300">Focus on communication skills</span>
                </li>
                <li className="flex items-start">
                  <span className="text-purple-400 mr-2">✓</span>
                  <span className="text-gray-300">Leadership development insights</span>
                </li>
              </ul>

              <div>
                <button 
                  onClick={openContactModal}
                  className="px-6 py-3 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl"
                >
                  Try Daily Bios
                </button>
              </div>
            </div>

            <div className="lg:w-1/2 flex items-center justify-center" style={{ marginTop: '-6rem' }}>
              <div className="w-full h-full flex items-center justify-center" style={{ transform: 'scale(1.25)' }}>
                <IPhonePreview />
              </div>
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Projects;
