import React from 'react';

interface BackgroundProps {
  children: React.ReactNode;
  className?: string;
}

const Background: React.FC<BackgroundProps> = ({ children, className = '' }) => {
  return (
    <div className={`relative ${className}`}>
      <div className="absolute inset-0 bg-gradient-to-b from-black via-black to-custom-purple z-0"></div>
      <div className="absolute inset-0 bg-gradient-custom animate-gradient-xy opacity-50 z-0"></div>

      {/* Animated gradient orbs */}
      <div className="absolute top-1/4 left-1/4 w-64 h-64 rounded-full bg-custom-pastel-green opacity-20 blur-3xl animate-pulse"></div>
      <div className="absolute bottom-1/3 right-1/4 w-80 h-80 rounded-full bg-custom-soft-pink opacity-20 blur-3xl animate-pulse" style={{ animationDelay: '1s' }}></div>
      <div className="absolute top-1/2 left-1/2 w-72 h-72 rounded-full bg-custom-teal opacity-20 blur-3xl animate-pulse" style={{ animationDelay: '2s' }}></div>

      <div className="relative z-10">
        {children}
      </div>
    </div>
  );
};

export default Background;
