import React from 'react';
import { Menu, X } from 'lucide-react';
import Logo from './Logo';

interface HeaderProps {
  isMenuOpen: boolean;
  setIsMenuOpen: (isOpen: boolean) => void;
  scrollToSection: (ref: React.RefObject<HTMLDivElement>) => void;
  projectsRef: React.RefObject<HTMLDivElement>;
  servicesRef: React.RefObject<HTMLDivElement>;
  contactRef: React.RefObject<HTMLDivElement>;
  openContactModal: () => void;
}

const Header: React.FC<HeaderProps> = ({ 
  isMenuOpen, 
  setIsMenuOpen, 
  scrollToSection,
  projectsRef,
  servicesRef,
  contactRef,
  openContactModal
}) => {
  return (
    <header className="fixed w-full z-50 bg-black bg-opacity-80 backdrop-blur-sm">
      <div className="container mx-auto px-4 py-4 flex justify-between items-center">
        <div className="flex items-center">
          <Logo />
        </div>

        {/* Desktop Navigation */}
        <nav className="hidden md:flex items-center space-x-8">
          <a 
            href="#" 
            className="text-white hover:text-purple-300 transition-colors duration-300 relative group"
            onClick={(e) => {
              e.preventDefault();
              window.scrollTo({ top: 0, behavior: 'smooth' });
            }}
          >
            Home
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-custom transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a 
            href="#projects" 
            className="text-white hover:text-purple-300 transition-colors duration-300 relative group"
            onClick={(e) => {
              e.preventDefault();
              scrollToSection(projectsRef);
            }}
          >
            Daily Bios
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-custom transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a 
            href="#services" 
            className="text-white hover:text-purple-300 transition-colors duration-300 relative group"
            onClick={(e) => {
              e.preventDefault();
              scrollToSection(servicesRef);
            }}
          >
            Service
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-custom transition-all duration-300 group-hover:w-full"></span>
          </a>
          <a 
            href="#contact" 
            className="text-white hover:text-purple-300 transition-colors duration-300 relative group"
            onClick={(e) => {
              e.preventDefault();
              scrollToSection(contactRef);
            }}
          >
            Contact
            <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-custom transition-all duration-300 group-hover:w-full"></span>
          </a>
        </nav>

        <button 
          onClick={openContactModal}
          className="hidden md:block px-6 py-2 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl"
        >
          Get Started
        </button>

        {/* Mobile Menu Button */}
        <button 
          className="md:hidden text-white"
          onClick={() => setIsMenuOpen(!isMenuOpen)}
        >
          {isMenuOpen ? <X size={24} /> : <Menu size={24} />}
        </button>
      </div>

      {/* Mobile Navigation */}
      {isMenuOpen && (
        <div className="md:hidden bg-black bg-opacity-95 absolute w-full">
          <div className="container mx-auto px-4 py-4 flex flex-col space-y-4">
            <a 
              href="#" 
              className="text-white py-2 hover:text-purple-300 transition-colors duration-300"
              onClick={(e) => {
                e.preventDefault();
                window.scrollTo({ top: 0, behavior: 'smooth' });
                setIsMenuOpen(false);
              }}
            >
              Home
            </a>
            <a 
              href="#projects" 
              className="text-white py-2 hover:text-purple-300 transition-colors duration-300"
              onClick={(e) => {
                e.preventDefault();
                scrollToSection(projectsRef);
              }}
            >
              Daily Bios
            </a>
            <a 
              href="#services" 
              className="text-white py-2 hover:text-purple-300 transition-colors duration-300"
              onClick={(e) => {
                e.preventDefault();
                scrollToSection(servicesRef);
              }}
            >
              Services
            </a>
            <a 
              href="#contact" 
              className="text-white py-2 hover:text-purple-300 transition-colors duration-300"
              onClick={(e) => {
                e.preventDefault();
                scrollToSection(contactRef);
              }}
            >
              Contact
            </a>
            <button 
              onClick={() => {
                openContactModal();
                setIsMenuOpen(false);
              }}
              className="px-6 py-2 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-left"
            >
              Get Started
            </button>
          </div>
        </div>
      )}
    </header>
  );
};

export default Header;
