import React, { useEffect, useRef, useState } from 'react';
import { Code, Palette, BarChart, Globe } from 'lucide-react';

interface ServicesProps {
  scrollToContact: () => void;
}

const Services: React.FC<ServicesProps> = ({ scrollToContact }) => {
  const [isVisible, setIsVisible] = useState(false);
  const sectionRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setIsVisible(true);
          observer.unobserve(entry.target);
        }
      },
      { threshold: 0.2 }
    );

    if (sectionRef.current) {
      observer.observe(sectionRef.current);
    }

    return () => {
      if (sectionRef.current) {
        observer.unobserve(sectionRef.current);
      }
    };
  }, []);

  const services = [
    {
      icon: <Code size={40} className="text-purple-400 group-hover:text-white transition-colors duration-300" />,
      title: 'Web Development',
      description: 'Custom web applications built with cutting-edge technologies to deliver exceptional user experiences and robust functionality.',
    },
    {
      icon: <Palette size={40} className="text-purple-400 group-hover:text-white transition-colors duration-300" />,
      title: 'UI/UX Design',
      description: 'Intuitive and visually stunning interfaces designed to engage users, enhance brand perception, and drive conversions.',
    },
    {
      icon: <BarChart size={40} className="text-purple-400 group-hover:text-white transition-colors duration-300" />,
      title: 'Digital Strategy',
      description: 'Data-driven strategies to optimize your digital presence, increase market reach, and achieve measurable business outcomes.',
    },
    {
      icon: <Globe size={40} className="text-purple-400 group-hover:text-white transition-colors duration-300" />,
      title: 'E-commerce Solutions',
      description: 'End-to-end e-commerce platforms that streamline operations, enhance customer experiences, and drive sales growth.',
    },
  ];

  return (
    <section id="services" ref={sectionRef} className="py-20 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-b from-purple-900 to-black z-0"></div>

      <div className="container mx-auto px-4 relative z-10">
        <div className={`text-center mb-16 transition-all duration-1000 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">Our Services</span>
          </h2>

          <p className="text-lg max-w-3xl mx-auto text-gray-300">
            We offer a comprehensive range of digital services to help your business thrive in today's competitive landscape.
          </p>
        </div>

        <div className={`grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8 mb-16 transition-all duration-1000 delay-300 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          {services.map((service, index) => (
            <div 
              key={index} 
              className="group bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl hover:from-purple-600 hover:to-blue-600 transition-all duration-500 cursor-pointer"
              onClick={scrollToContact}
            >
              <div className="bg-black rounded-lg h-full p-6 flex flex-col">
                <div className="mb-4">
                  {service.icon}
                </div>

                <h3 className="text-xl font-bold mb-3 group-hover:bg-clip-text group-hover:text-transparent group-hover:bg-gradient-to-r group-hover:from-purple-400 group-hover:to-blue-400 transition-all duration-500">
                  {service.title}
                </h3>

                <p className="text-gray-400 flex-grow">
                  {service.description}
                </p>

                <div className="mt-4 overflow-hidden">
                  <span className="inline-block text-purple-400 group-hover:translate-x-2 transition-transform duration-300">
                    Learn more â†’
                  </span>
                </div>
              </div>
            </div>
          ))}
        </div>

        <div className={`text-center transition-all duration-1000 delay-600 ${isVisible ? 'translate-y-0 opacity-100' : 'translate-y-10 opacity-0'}`}>
          <button 
            onClick={scrollToContact}
            className="px-8 py-3 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-lg font-medium"
          >
            Consult Us
          </button>
        </div>
      </div>
    </section>
  );
};

export default Services;
