import React, { useState } from 'react';
import { 
  Smartphone, 
  Globe, 
  Calendar, 
  DollarSign, 
  List, 
  MessageSquare, 
  Share2, 
  Phone,
  Users,
  Palette,
  Database
} from 'lucide-react';

interface QuoteProps {
  scrollToContact: () => void;
}

const Quote: React.FC<QuoteProps> = ({ scrollToContact }) => {
  const [formData, setFormData] = useState({
    appType: '',
    completionDate: '',
    budget: '',
    features: [] as string[],
    additionalInfo: '',
    referral: '',
    followUp: false
  });

  const [isSubmitting, setIsSubmitting] = useState(false);
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleFeatureToggle = (feature: string) => {
    setFormData(prev => ({
      ...prev,
      features: prev.features.includes(feature)
        ? prev.features.filter(f => f !== feature)
        : [...prev.features, feature]
    }));
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/send-quote', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        setIsSubmitted(true);
      } else {
        throw new Error('Failed to submit');
      }
    } catch (error) {
      console.error('Error submitting form:', error);
      alert('There was an error submitting your quote request. Please try again.');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (isSubmitted) {
    return (
      <section id="quote" className="py-20 relative overflow-hidden">
        <div className="absolute inset-0 bg-gradient-to-b from-purple-900 to-black z-0"></div>
        <div className="container mx-auto px-4 relative z-10">
          <div className="max-w-2xl mx-auto text-center">
            <h2 className="text-3xl md:text-4xl font-bold mb-6">
              <span className="bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                Thank You for Reaching Out!
              </span>
            </h2>
            <p className="text-lg text-gray-300 mb-8">
              We've received your details and will be in touch soon with a custom quote.
            </p>
            <button
              onClick={() => setIsSubmitted(false)}
              className="px-8 py-3 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-lg font-medium"
            >
              Submit Another Quote
            </button>
          </div>
        </div>
      </section>
    );
  }

  return (
    <section id="quote" className="py-20 relative overflow-hidden">
      <div className="absolute inset-0 bg-gradient-to-b from-purple-900 to-black z-0"></div>

      <div className="container mx-auto px-4 relative z-10">
        <div className="text-center mb-16">
          <h2 className="text-3xl md:text-4xl font-bold mb-6">
            <span className="bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
              Get a Custom Quote for Your App Project
            </span>
          </h2>

          <p className="text-lg max-w-3xl mx-auto text-gray-300">
            We're here to bring your vision to life! Please fill out the form below to provide us with the details of your project, and we will get back to you with a custom quote tailored to your needs.
          </p>
        </div>

        <form onSubmit={handleSubmit} className="max-w-3xl mx-auto">
          <div className="space-y-8">
            {/* App Type */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  What Type of App Do You Need?
                </label>
                <select
                  value={formData.appType}
                  onChange={(e) => setFormData({ ...formData, appType: e.target.value })}
                  className="w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  required
                >
                  <option value="">Select an option</option>
                  <option value="android">Android App</option>
                  <option value="ios">iOS App</option>
                  <option value="web">Web App</option>
                  <option value="other">Other</option>
                </select>
                {formData.appType === 'other' && (
                  <input
                    type="text"
                    placeholder="Please specify"
                    className="mt-3 w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                    onChange={(e) => setFormData({ ...formData, appType: e.target.value })}
                  />
                )}
              </div>
            </div>

            {/* Completion Date */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  What Is Your Desired Completion Date?
                </label>
                <input
                  type="date"
                  value={formData.completionDate}
                  onChange={(e) => setFormData({ ...formData, completionDate: e.target.value })}
                  className="w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  required
                />
              </div>
            </div>

            {/* Budget */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  What Is Your Estimated Budget?
                </label>
                <select
                  value={formData.budget}
                  onChange={(e) => setFormData({ ...formData, budget: e.target.value })}
                  className="w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  required
                >
                  <option value="">Select a budget range</option>
                  <option value="under-5000">Under $5,000</option>
                  <option value="5000-10000">$5,000 - $10,000</option>
                  <option value="10000-20000">$10,000 - $20,000</option>
                  <option value="above-20000">Above $20,000</option>
                  <option value="other">Other</option>
                </select>
                {formData.budget === 'other' && (
                  <input
                    type="text"
                    placeholder="Please specify your budget"
                    className="mt-3 w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                    onChange={(e) => setFormData({ ...formData, budget: e.target.value })}
                  />
                )}
              </div>
            </div>

            {/* Features */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  What Features/Functionality Do You Want?
                </label>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {[
                    { id: 'notifications', label: 'Push Notifications', icon: <MessageSquare size={20} /> },
                    { id: 'accounts', label: 'User Accounts (Login/Sign Up)', icon: <Users size={20} /> },
                    { id: 'purchases', label: 'In-App Purchases', icon: <DollarSign size={20} /> },
                    { id: 'maps', label: 'GPS/Maps Integration', icon: <Globe size={20} /> },
                    { id: 'social', label: 'Social Media Integration', icon: <Share2 size={20} /> },
                    { id: 'design', label: 'Custom Design', icon: <Palette size={20} /> },
                    { id: 'database', label: 'Database Storage', icon: <Database size={20} /> },
                  ].map((feature) => (
                    <label
                      key={feature.id}
                      className="flex items-center space-x-3 p-3 bg-gray-800 rounded-lg cursor-pointer hover:bg-gray-700 transition-colors"
                    >
                      <input
                        type="checkbox"
                        checked={formData.features.includes(feature.id)}
                        onChange={() => handleFeatureToggle(feature.id)}
                        className="form-checkbox h-5 w-5 text-purple-400 rounded focus:ring-purple-400"
                      />
                      <span className="text-white">{feature.label}</span>
                    </label>
                  ))}
                </div>
              </div>
            </div>

            {/* Additional Info */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  Any Additional Information or Special Requests?
                </label>
                <textarea
                  value={formData.additionalInfo}
                  onChange={(e) => setFormData({ ...formData, additionalInfo: e.target.value })}
                  className="w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400 h-32"
                  placeholder="Please provide any additional details about your project..."
                />
              </div>
            </div>

            {/* Referral */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  How Did You Hear About Us?
                </label>
                <select
                  value={formData.referral}
                  onChange={(e) => setFormData({ ...formData, referral: e.target.value })}
                  className="w-full bg-gray-800 text-white rounded-lg p-3 focus:outline-none focus:ring-2 focus:ring-purple-400"
                  required
                >
                  <option value="">Select an option</option>
                  <option value="google">Google Search</option>
                  <option value="social">Social Media</option>
                  <option value="referral">Referral</option>
                  <option value="other">Other</option>
                </select>
              </div>
            </div>

            {/* Follow-up */}
            <div className="bg-gradient-to-br from-gray-900 to-black p-0.5 rounded-xl">
              <div className="bg-black rounded-lg p-6">
                <label className="block text-xl font-bold mb-4 bg-clip-text text-transparent bg-gradient-to-r from-purple-400 to-blue-400">
                  Would You Like to Receive a Follow-Up Call?
                </label>
                <div className="flex items-center space-x-4">
                  <label className="flex items-center space-x-2">
                    <input
                      type="radio"
                      checked={formData.followUp}
                      onChange={() => setFormData({ ...formData, followUp: true })}
                      className="form-radio h-5 w-5 text-purple-400 focus:ring-purple-400"
                    />
                    <span className="text-white">Yes</span>
                  </label>
                  <label className="flex items-center space-x-2">
                    <input
                      type="radio"
                      checked={!formData.followUp}
                      onChange={() => setFormData({ ...formData, followUp: false })}
                      className="form-radio h-5 w-5 text-purple-400 focus:ring-purple-400"
                    />
                    <span className="text-white">No</span>
                  </label>
                </div>
              </div>
            </div>
          </div>

          <div className="mt-8 text-center">
            <button
              type="submit"
              disabled={isSubmitting}
              className="px-8 py-3 rounded-full bg-white text-black hover:bg-gray-100 transform hover:scale-105 transition-all duration-300 shadow-lg hover:shadow-xl text-lg font-medium disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isSubmitting ? 'Submitting...' : 'Submit Quote Request'}
            </button>
          </div>
        </form>
      </div>
    </section>
  );
};

export default Quote;
